<script type="text/javascript">
    $("#Submit").on("click", function() {

        $('#Submit').html('ĐANG XỬ LÝ').prop('disabled',
            true);
        $.ajax({
            url: "<?= BASE_URL("assets/ajaxs/CayThue.php"); ?>",
            method: "POST",
            data: {
                type: 'Order',
                tk: $("#tk").val(),
                mk: $("#mk").val(),
                ghichu: $("#ghichu").val(),
                dichvu: $("#dichvu").val()
            },
            success: function(response) {
                $("#thongbao").html(response);
                $('#Submit').html('<i class="fa fa-cart-plus mr-1" aria-hidden="true"></i> THANH TOÁN').prop('disabled', false);
            }
        });
    });
</script>

